import React, { useState, useEffect } from "react";
import "./index.css";
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const App = () => {
  const [inputs, setInputs] = useState({
    receita: 150000,
    lucro_liquido: 15000,
    estado: "SP",
    setor: "servicos",
  });
  const [resultado, setResultado] = useState(null);

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:5000/simulate_regimes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(inputs),
      });
      const data = await response.json();
      setResultado(data);
    } catch (error) {
      console.error("Erro ao buscar dados:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [inputs]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs((prev) => ({ ...prev, [name]: value }));
  };

  // Preparação do gráfico
  const dataGraficoBarra = resultado
    ? {
        labels: ["Lucro Presumido", "Lucro Real", "Novo Regime", "Simples Nacional"],
        datasets: [
          {
            label: "Comparação de Impostos",
            data: [
              resultado.imposto_presumido,
              resultado.imposto_real,
              resultado.imposto_novo,
              resultado.simples_nacional,
            ],
            backgroundColor: ["#6c757d", "#ffc107", "#007bff", "#28a745"],
          },
        ],
      }
    : null;

  // Encontrar o regime mais vantajoso
  const regimes = [
    { nome: "Lucro Presumido", valor: resultado?.imposto_presumido },
    { nome: "Lucro Real", valor: resultado?.imposto_real },
    { nome: "Novo Regime", valor: resultado?.imposto_novo },
    { nome: "Simples Nacional", valor: resultado?.simples_nacional },
  ];

  const regimeMaisVantajoso = regimes.reduce((min, regime) =>
    regime.valor !== undefined && regime.valor < min.valor ? regime : min
  );

  return (
    <div className="container">
      <h1 className="titulo">Simulação Tributária</h1>
      <div className="layout">
        {/* Coluna Esquerda */}
        <div className="coluna-esquerda">
          <div className="inputs">
            <h2>Entradas</h2>
            <label>Receita Bruta Anual: R$ {inputs.receita}</label>
            <input
              type="range"
              name="receita"
              min="50000"
              max="1000000"
              value={inputs.receita}
              onChange={handleInputChange}
            />

            <label>Lucro Líquido (Lucro Real): R$ {inputs.lucro_liquido}</label>
            <input
              type="range"
              name="lucro_liquido"
              min="10000"
              max="500000"
              value={inputs.lucro_liquido}
              onChange={handleInputChange}
            />

            <label>Estado:</label>
            <select name="estado" value={inputs.estado} onChange={handleInputChange}>
              <option value="SP">São Paulo</option>
              <option value="RJ">Rio de Janeiro</option>
              <option value="MG">Minas Gerais</option>
              <option value="default">Outro</option>
            </select>

            <label>Setor Econômico:</label>
            <select name="setor" value={inputs.setor} onChange={handleInputChange}>
              <option value="default">Geral</option>
              <option value="educacao">Educação</option>
              <option value="saude">Saúde</option>
              <option value="industria">Indústria</option>
              <option value="comercio">Comércio</option>
              <option value="servicos">Serviços</option>
            </select>
          </div>
        </div>

        {/* Coluna Direita */}
        <div className="coluna-direita">
          {resultado && (
            <>
              <div className="grafico">
                <h3>Comparação de Impostos</h3>
                <Bar data={dataGraficoBarra} />
              </div>
              <div className="tabela">
                <h3>Detalhamento dos Tributos</h3>
                <table>
                  <thead>
                    <tr>
                      <th>Regime</th>
                      <th>Imposto Total</th>
                      <th>% da Receita</th>
                    </tr>
                  </thead>
                  <tbody>
                    {regimes.map((regime, index) => (
                      <tr key={index}>
                        <td>{regime.nome}</td>
                        <td>R$ {regime.valor?.toFixed(2) || "-"}</td>
                        <td>{((regime.valor / inputs.receita) * 100).toFixed(2)}%</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <p className="destaque">
                  Regime mais vantajoso: <strong>{regimeMaisVantajoso.nome}</strong> com imposto total de R${" "}
                  {regimeMaisVantajoso.valor?.toFixed(2)}.
                </p>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
