import React, { useState, useEffect } from "react";
import "./index.css";
import { Chart as ChartJS, BarElement, ArcElement, CategoryScale, LinearScale, Tooltip, Legend } from "chart.js";
import { Bar, Pie } from "react-chartjs-2";

// Registrar os componentes necessários para o gráfico
ChartJS.register(BarElement, ArcElement, CategoryScale, LinearScale, Tooltip, Legend);


const App = () => {
  const [inputs, setInputs] = useState({
    receita: 150000,
    lucro_liquido: 15000,
    estado: "SP",
    setor: "default",
  });
  const [resultado, setResultado] = useState(null);

  // Requisição ao backend dinamicamente
  useEffect(() => {
    const fetchData = async () => {
      if (inputs.receita) {
        try {
          const response = await fetch("http://localhost:5000/simulate_regimes", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(inputs),
          });
          const data = await response.json();
          setResultado(data);
        } catch (error) {
          console.error("Erro ao calcular:", error);
        }
      }
    };
    fetchData();
  }, [inputs]);

  // Dados para o gráfico de barras
  const dataGraficoBarra = resultado
    ? {
        labels: ["Lucro Presumido", "Lucro Real", "Novo Regime (IBS + CBS)"],
        datasets: [
          {
            label: "Comparação de Impostos",
            data: [resultado.imposto_presumido, resultado.imposto_real, resultado.imposto_novo],
            backgroundColor: ["#6c757d", "#ffc107", "#007bff"],
          },
        ],
      }
    : null;

  // Dados para o gráfico de pizza
  const dataGraficoPizza = resultado
    ? {
        labels: ["IBS + CBS", "Lucro Presumido", "Lucro Real"],
        datasets: [
          {
            label: "Distribuição de Impostos",
            data: [resultado.imposto_novo, resultado.imposto_presumido, resultado.imposto_real],
            backgroundColor: ["#007bff", "#6c757d", "#ffc107"],
          },
        ],
      }
    : null;

  return (
    <div className="container">
      <h1 className="titulo">Simulação de Regimes Tributários</h1>
      <div className="layout">
        {/* Inputs com Sliders */}
        <div className="inputs">
          <label>Receita Bruta Anual: R$ {inputs.receita}</label>
          <input
            type="range"
            min="50000"
            max="1000000"
            value={inputs.receita}
            onChange={(e) => setInputs({ ...inputs, receita: e.target.value })}
          />
          <label>Lucro Líquido (Lucro Real): R$ {inputs.lucro_liquido}</label>
          <input
            type="range"
            min="5000"
            max="500000"
            value={inputs.lucro_liquido}
            onChange={(e) => setInputs({ ...inputs, lucro_liquido: e.target.value })}
          />
          <label>Estado:</label>
          <select
            value={inputs.estado}
            onChange={(e) => setInputs({ ...inputs, estado: e.target.value })}
          >
            <option value="SP">São Paulo</option>
            <option value="RJ">Rio de Janeiro</option>
            <option value="MG">Minas Gerais</option>
            <option value="default">Outro</option>
          </select>
        </div>

        {/* Resultados */}
        {resultado && (
          <div className="resultado">
            <h3>Resumo Comparativo</h3>
            <table>
              <thead>
                <tr>
                  <th>Regime</th>
                  <th>Imposto Total</th>
                  <th>Alíquota Efetiva</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Lucro Presumido</td>
                  <td>R$ {resultado.imposto_presumido.toFixed(2)}</td>
                  <td>25%</td>
                </tr>
                <tr>
                  <td>Lucro Real</td>
                  <td>R$ {resultado.imposto_real.toFixed(2)}</td>
                  <td>34%</td>
                </tr>
                <tr>
                  <td>Novo Regime (IBS + CBS)</td>
                  <td>R$ {resultado.imposto_novo.toFixed(2)}</td>
                  <td>30%</td>
                </tr>
              </tbody>
            </table>
            <div className="grafico">
              <h3>Gráfico Comparativo</h3>
              <Bar data={dataGraficoBarra} />
            </div>
            <div className="grafico">
              <h3>Distribuição de Impostos</h3>
              <Pie data={dataGraficoPizza} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
