import React, { useState, useEffect } from "react";
import "./index.css";
import { Chart as ChartJS, BarElement, ArcElement, CategoryScale, LinearScale, Tooltip, Legend } from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(BarElement, ArcElement, CategoryScale, LinearScale, Tooltip, Legend);

const App = () => {
  const [inputs, setInputs] = useState({
    receita: 150000,
    lucro_liquido: 15000,
    estado: "SP",
    setor: "servicos",
  });
  const [resultado, setResultado] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      if (inputs.receita) {
        const response = await fetch("http://localhost:5000/simulate_regimes", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(inputs),
        });
        const data = await response.json();
        setResultado(data);
      }
    };
    fetchData();
  }, [inputs]);

  const dataGraficoBarra = resultado
    ? {
        labels: ["Lucro Presumido", "Lucro Real", "Novo Regime"],
        datasets: [
          {
            label: "Comparação de Impostos",
            data: [resultado.imposto_presumido, resultado.imposto_real, resultado.imposto_novo],
            backgroundColor: ["#6c757d", "#ffc107", "#007bff"],
          },
        ],
      }
    : null;

  return (
    <div className="container">
      <h1 className="titulo">Simulação Tributária</h1>
      <div className="layout">
        {/* Coluna da Esquerda */}
        <div className="coluna-esquerda">
          <div className="inputs">
            <h2>Entradas</h2>
            <label>Receita Bruta Anual: R$ {inputs.receita}</label>
            <input
              type="range"
              min="50000"
              max="1000000"
              value={inputs.receita}
              onChange={(e) => setInputs({ ...inputs, receita: e.target.value })}
            />
            <label>Lucro Líquido (Lucro Real): R$ {inputs.lucro_liquido}</label>
            <input
              type="range"
              min="10000"
              max="500000"
              value={inputs.lucro_liquido}
              onChange={(e) => setInputs({ ...inputs, lucro_liquido: e.target.value })}
            />
            <label>Estado:</label>
            <select
              value={inputs.estado}
              onChange={(e) => setInputs({ ...inputs, estado: e.target.value })}
            >
              <option value="SP">São Paulo</option>
              <option value="RJ">Rio de Janeiro</option>
              <option value="MG">Minas Gerais</option>
              <option value="default">Outro</option>
            </select>
            <label>Setor Econômico:</label>
            <select
              value={inputs.setor}
              onChange={(e) => setInputs({ ...inputs, setor: e.target.value })}
            >
              <option value="default">Geral</option>
              <option value="educacao">Educação</option>
              <option value="saude">Saúde</option>
              <option value="industria">Indústria</option>
              <option value="comercio">Comércio</option>
              <option value="servicos">Serviços</option>
            </select>
          </div>

          {/* Explicações */}
          <div className="explicacoes">
            <h2>Explicações</h2>
            <p>
              Compare os regimes tributários atuais com o novo regime tributário
              (IBS + CBS) proposto no PLP 68/2024.
            </p>
            <p>
              <strong>Lucro Presumido:</strong> Base de cálculo simplificada, com alíquotas fixas
              sobre a receita.
            </p>
            <p>
              <strong>Lucro Real:</strong> Cálculo sobre o lucro líquido da empresa com alíquota de IRPJ e CSLL.
            </p>
            <p>
              <strong>Novo Regime (IBS + CBS):</strong> Substitui PIS, COFINS, ICMS e ISS com alíquota unificada.
            </p>
          </div>
        </div>

        {/* Coluna da Direita */}
        <div className="coluna-direita">
          {resultado && (
            <>
              <div className="grafico">
                <h3>Comparação de Impostos</h3>
                <Bar data={dataGraficoBarra} />
              </div>
              <div className="tabela">
                <h3>Detalhamento dos Tributos</h3>
                <table>
                  <thead>
                    <tr>
                      <th>Tributo</th>
                      <th>Valor</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>Lucro Presumido</td>
                      <td>R$ {resultado.imposto_presumido.toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Lucro Real</td>
                      <td>R$ {resultado.imposto_real.toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Novo Regime</td>
                      <td>R$ {resultado.imposto_novo.toFixed(2)}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
