import React, { useState, useEffect } from "react";
import "./index.css";
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from "chart.js";
import { Bar } from "react-chartjs-2";
import aliquotas from "./aliquotas.json";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

// Componente Modal para Disclaimer
const DisclaimerModal = ({ isOpen, onClose }) => {
  return (
    isOpen && (
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <h2>Disclaimer Jurídico e Limitações</h2>
          <p>
            <strong>Limitações do Sistema de Simulação Tributária:</strong>
            <br />
            - <em>Dados Atualizados:</em> As alíquotas e legislações usadas no sistema são baseadas na última atualização disponível. No entanto, mudanças legislativas podem ocorrer frequentemente, e nosso sistema pode não refletir essas mudanças em tempo real.
            <br />
            - <em>Simples Nacional:</em> O cálculo considera apenas as faixas de receita e alíquotas gerais, não levando em conta anexos específicos.
            <br />
            - <em>Novo Regime (IBS e CBS):</em> As alíquotas são baseadas em propostas legislativas (PL 68/2024), que ainda não foram implementadas.
            <br />
            - <em>ICMS e ISS:</em> Utilizamos valores médios ou conhecidos, mas alíquotas podem variar por estado e município.
            <br />
            - <em>Lucro Presumido e Real:</em> Cálculos simplificados, não considerando todas as deduções ou créditos fiscais.
            <br />
            - <em>Simulações:</em> Resultados são aproximações, não substituem consultoria profissional.
            <br />
            <strong>Disclaimer Jurídico:</strong>
            <br />
            Este sistema é uma ferramenta de simulação e não pretende substituir a orientação legal ou contábil profissional. Não nos responsabilizamos por quaisquer decisões fiscais ou financeiras baseadas nas informações fornecidas aqui. As alíquotas e legislações podem mudar, e é responsabilidade do usuário verificar a precisão das informações com fontes oficiais ou consultores especializados antes de tomar decisões baseadas nesta simulação.
          </p>
          <button onClick={onClose}>Fechar</button>
        </div>
      </div>
    )
  );
};

const App = () => {
  const [inputs, setInputs] = useState({
    receita: 1000000,
    lucro_liquido: 50000,
    estado: "SP",
    setor: "servicos",
  });
  const [resultado, setResultado] = useState(null);
  const [showDisclaimer, setShowDisclaimer] = useState(true);
  const limiteSimples = 4800000; // Limite para o Simples Nacional

  useEffect(() => {
    const fetchData = async () => {
      console.log("Enviando para o backend:", inputs); // Log dos inputs
      try {
        const response = await fetch("http://localhost:5000/simulate_regimes", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(inputs),
        });
        const data = await response.json();
        console.log("Resposta do backend:", data); // Log da resposta
        setResultado(data);
      } catch (error) {
        console.error("Erro ao buscar dados:", error);
      }
    };
    fetchData();
  }, [inputs]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs((prev) => ({ ...prev, [name]: value }));
  };

  const resetInputs = () => {
    setInputs({
      receita: 1000000,
      lucro_liquido: 50000,
      estado: "SP",
      setor: "servicos",
    });
  };

  const explicacaoDinamica = () => {
    const estadoSelecionado = inputs.estado;
    const setorSelecionado = inputs.setor;

    const icms = aliquotas.estados[estadoSelecionado]?.ICMS || 0.18;
    const iss = aliquotas.setores[setorSelecionado]?.ISS || 0.02;
    const ibs = aliquotas.estados[estadoSelecionado]?.IBS || 0.18;

    return (
      <>
        <p>
          <strong>Lucro Presumido:</strong> Alíquota de ICMS: {icms * 100}%, ISS: {iss * 100}%.
        </p>
        <p>
          <strong>Lucro Real:</strong> Base de cálculo sobre o lucro líquido da empresa com alíquota total de 34%.
        </p>
        <p>
          <strong>Novo Regime (IBS + CBS):</strong> IBS: {ibs * 100}% + CBS: 12% (total: {ibs * 100 + 12}%).
        </p>
      </>
    );
  };

  const regimes = [
    { nome: "Lucro Presumido", valor: resultado?.imposto_presumido },
    { nome: "Lucro Real", valor: resultado?.imposto_real },
    { nome: "Novo Regime", valor: resultado?.imposto_novo },
  ];

  if (inputs.receita <= limiteSimples) {
    regimes.push({ nome: "Simples Nacional", valor: resultado?.simples_nacional });
  }

  const regimeMaisVantajoso = resultado
    ? regimes.reduce((min, regime) => (regime.valor < min.valor ? regime : min), regimes[0])
    : null;

  const dataGraficoBarra = resultado
    ? {
        labels: regimes.map((regime) => regime.nome),
        datasets: [
          {
            label: "Comparação de Impostos",
            data: regimes.map((regime) => regime.valor),
            backgroundColor: ["#6c757d", "#ffc107", "#007bff", "#28a745"],
          },
        ],
      }
    : null;

  return (
    <div className="container">
      <DisclaimerModal isOpen={showDisclaimer} onClose={() => setShowDisclaimer(false)} />
      <h1 className="titulo">Simulação Tributária</h1>
      <div className="layout">
        <div className="coluna-esquerda">
          <div className="inputs">
            <h2>Entradas</h2>
            <label>Receita Bruta Anual: R$ {parseFloat(inputs.receita).toLocaleString()}</label>
            <input
              type="range"
              name="receita"
              min="50000"
              max="10000000"
              step="1000"
              value={inputs.receita}
              onChange={handleInputChange}
            />
            <label>Lucro Líquido (Lucro Real): R$ {parseFloat(inputs.lucro_liquido).toLocaleString()}</label>
            <input
              type="range"
              name="lucro_liquido"
              min="10000"
              max="5000000"
              step="1000"
              value={inputs.lucro_liquido}
              onChange={handleInputChange}
            />
            <label>Estado:</label>
            <select name="estado" value={inputs.estado} onChange={handleInputChange}>
              {Object.keys(aliquotas.estados).map((estado) => (
                <option key={estado} value={estado}>
                  {estado}
                </option>
              ))}
            </select>
            <label>Setor Econômico:</label>
            <select name="setor" value={inputs.setor} onChange={handleInputChange}>
              {Object.keys(aliquotas.setores).map((setor) => (
                <option key={setor} value={setor}>
                  {setor.charAt(0).toUpperCase() + setor.slice(1)}
                </option>
              ))}
            </select>            
          </div>
          <div className="explicacoes">
            <h2>Explicações</h2>
            {explicacaoDinamica()}
            {inputs.receita > limiteSimples && (
              <p className="alerta">
                O Simples Nacional não está disponível para receitas superiores a R$ 4.800.000,00.
              </p>
            )}
          </div>
        </div>
        <div className="coluna-direita">
          {resultado && (
            <>
              <div className="grafico">
                <h3>Comparação de Impostos</h3>
                <Bar data={dataGraficoBarra} 
                //key={JSON.stringify(dataGraficoBarra)} // Força o re-render
                />
              </div>
              <div className="tabela">
                <h3>Detalhamento dos Tributos</h3>
               
                <table>
                  <thead>
                    <tr>
                      <th>Regime</th>
                      <th>Imposto Total</th>
                      <th>% da Receita</th>
                    </tr>
                  </thead>
                  <tbody>
                    {regimes.map((regime) => (
                      <tr key={regime.nome}>
                        <td>{regime.nome}</td>
                        <td>R$ {regime.valor?.toFixed(2)}</td>
                        <td>{((regime.valor / inputs.receita) * 100).toFixed(2)}%</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <div class="disclaimer-box">
              <h3>Importante</h3>
              <p>
                Este sistema é uma ferramenta de simulação e não substitui a orientação legal ou contábil profissional.
                Os resultados são aproximações baseadas nas legislações atuais e propostas. Consulte fontes oficiais para informações detalhadas.
              </p>
            </div>

            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
