import React, { useState } from "react";
import "./index.css";
import { Bar } from "react-chartjs-2";

const App = () => {
  const [inputs, setInputs] = useState({
    receita: "",
    lucro_liquido: "",
    estado: "SP",
    setor: "default",
  });
  const [resultado, setResultado] = useState(null);

  const calcular = async () => {
    try {
      const response = await fetch("http://localhost:5000/simulate_regimes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(inputs),
      });
      const data = await response.json();
      setResultado(data);
    } catch (error) {
      console.error("Erro ao calcular:", error);
    }
  };

  const dataGrafico = resultado
    ? {
        labels: ["Lucro Presumido", "Lucro Real", "Novo Regime (IBS + CBS)"],
        datasets: [
          {
            label: "Comparação de Impostos",
            data: [
              resultado.imposto_presumido,
              resultado.imposto_real,
              resultado.imposto_novo,
            ],
            backgroundColor: ["#6c757d", "#ffc107", "#007bff"],
          },
        ],
      }
    : null;

  return (
    <div className="container">
      <h1 className="titulo">Simulação de Regimes Tributários</h1>
      <p className="descricao">
        Compare os regimes tributários <strong>antigos</strong> (Lucro Presumido e Lucro Real)
        com o novo regime tributário proposto pelo PLP 68/2024 (IBS + CBS).
      </p>
      <div className="formulario">
        <input
          type="number"
          placeholder="Receita Bruta Anual"
          value={inputs.receita}
          onChange={(e) => setInputs({ ...inputs, receita: e.target.value })}
          className="campo-entrada"
        />
        <input
          type="number"
          placeholder="Lucro Líquido (Lucro Real)"
          value={inputs.lucro_liquido}
          onChange={(e) =>
            setInputs({ ...inputs, lucro_liquido: e.target.value })
          }
          className="campo-entrada"
        />
        <select
          value={inputs.estado}
          onChange={(e) => setInputs({ ...inputs, estado: e.target.value })}
          className="campo-entrada"
        >
          <option value="SP">São Paulo</option>
          <option value="RJ">Rio de Janeiro</option>
          <option value="MG">Minas Gerais</option>
          <option value="default">Outro</option>
        </select>
        <select
          value={inputs.setor}
          onChange={(e) => setInputs({ ...inputs, setor: e.target.value })}
          className="campo-entrada"
        >
          <option value="default">Geral</option>
          <option value="educacao">Educação</option>
          <option value="saude">Saúde</option>
          <option value="industria">Indústria</option>
          <option value="comercio">Comércio</option>
          <option value="servicos">Serviços</option>
        </select>
      </div>
      <button onClick={calcular} className="botao-calcular">
        Calcular
      </button>

      {resultado && (
        <div className="resultado">
          <h2>Resultado da Simulação</h2>
          <div className="cards">
            <div className="card antigo">
              <h3>Lucro Presumido 💰</h3>
              <p><strong>R$ {resultado.imposto_presumido.toFixed(2)}</strong></p>
            </div>
            <div className="card antigo">
              <h3>Lucro Real ⚖️</h3>
              <p><strong>R$ {resultado.imposto_real.toFixed(2)}</strong></p>
            </div>
            <div className="card novo">
              <h3>Novo Regime (IBS + CBS) 📊</h3>
              <p><strong>R$ {resultado.imposto_novo.toFixed(2)}</strong></p>
            </div>
          </div>
          <div className="grafico">
            <h3>Comparação Gráfica</h3>
            <Bar data={dataGrafico} />
          </div>
          <div className="detalhes">
            <h3>Detalhes</h3>
            <p><strong>Lucro Presumido:</strong> {resultado.explicacoes.lucro_presumido}</p>
            <p><strong>Lucro Real:</strong> {resultado.explicacoes.lucro_real}</p>
            <p><strong>Novo Regime:</strong> {resultado.explicacoes.novo_regime}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
