import React, { useState, useEffect } from "react";
import "./index.css";
import { Chart as ChartJS, BarElement, ArcElement, CategoryScale, LinearScale, Tooltip, Legend } from "chart.js";
import { Bar, Pie } from "react-chartjs-2";

ChartJS.register(BarElement, ArcElement, CategoryScale, LinearScale, Tooltip, Legend);

const App = () => {
  const [inputs, setInputs] = useState({
    receita: 150000,
    lucro_liquido: 15000,
    estado: "SP",
    setor: "servicos",
  });
  const [resultado, setResultado] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      if (inputs.receita) {
        const response = await fetch("http://localhost:5000/simulate_regimes", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(inputs),
        });
        const data = await response.json();
        setResultado(data);
      }
    };
    fetchData();
  }, [inputs]);

  const dataGraficoBarra = resultado
    ? {
        labels: ["Lucro Presumido", "Lucro Real", "Novo Regime"],
        datasets: [
          {
            label: "Comparação de Impostos",
            data: [resultado.imposto_presumido, resultado.imposto_real, resultado.imposto_novo],
            backgroundColor: ["#6c757d", "#ffc107", "#007bff"],
          },
        ],
      }
    : null;

  return (
    <div className="container">
      <h1>Simulação Tributária: Regime Atual x Novo Regime</h1>

      <div className="inputs">
        <label>Receita Bruta: R$ {inputs.receita}</label>
        <input
          type="range"
          min="50000"
          max="1000000"
          value={inputs.receita}
          onChange={(e) => setInputs({ ...inputs, receita: e.target.value })}
        />

        <label>Lucro Líquido: R$ {inputs.lucro_liquido}</label>
        <input
          type="range"
          min="10000"
          max="500000"
          value={inputs.lucro_liquido}
          onChange={(e) => setInputs({ ...inputs, lucro_liquido: e.target.value })}
        />
      </div>

      {resultado && (
        <div className="resultado">
          <h3>Comparação Detalhada</h3>
          <table>
            <thead>
              <tr>
                <th>Tributo</th>
                <th>Valor</th>
              </tr>
            </thead>
            <tbody>
              <tr><td>PIS/COFINS</td><td>R$ {resultado.detalhes.PIS_COFINS.toFixed(2)}</td></tr>
              <tr><td>ICMS</td><td>R$ {resultado.detalhes.ICMS.toFixed(2)}</td></tr>
              <tr><td>ISS</td><td>R$ {resultado.detalhes.ISS.toFixed(2)}</td></tr>
              <tr><td>IRPJ/CSLL</td><td>R$ {resultado.detalhes.IRPJ_CSLL.toFixed(2)}</td></tr>
              <tr><td>Novo Regime</td><td>{resultado.detalhes.Novo_Regime_Alíquota}</td></tr>
            </tbody>
          </table>
          <div className="grafico">
            <h4>Comparação de Impostos</h4>
            <Bar data={dataGraficoBarra} />
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
