import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [inputs, setInputs] = useState({ receita: "", estado: "SP", setor: "default" });
  const [resultado, setResultado] = useState(null);

  const calcular = async () => {
    try {
      const response = await fetch("http://localhost:5000/calculate_pj", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(inputs),
      });
      const data = await response.json();
      setResultado(data);
    } catch (error) {
      console.error("Erro ao calcular:", error);
    }
  };

  return (
    <div className="container">
      <h1>Calculadora Tribut√°ria para Empresas (PJ)</h1>
      <div>
        <input
          type="number"
          placeholder="Receita Bruta Anual"
          value={inputs.receita}
          onChange={(e) => setInputs({ ...inputs, receita: e.target.value })}
        />
        <select
          value={inputs.estado}
          onChange={(e) => setInputs({ ...inputs, estado: e.target.value })}
        >
          <option value="SP">S√£o Paulo</option>
          <option value="RJ">Rio de Janeiro</option>
          <option value="MG">Minas Gerais</option>
          <option value="default">Outro</option>
        </select>
        <select
          value={inputs.setor}
          onChange={(e) => setInputs({ ...inputs, setor: e.target.value })}
        >
          <option value="default">Geral</option>
          <option value="educacao">Educa√ß√£o</option>
          <option value="saude">Sa√∫de</option>
        </select>
      </div>
      <button onClick={calcular}>Calcular</button>

      {resultado && (
        <div className="resultado">
          <h2>Resultado</h2>
          <p><strong>Receita:</strong> R$ {parseFloat(resultado.receita).toLocaleString()}</p>
          <p><strong>Imposto Atual:</strong> R$ {resultado.imposto_atual.toFixed(2)}</p>
          <p><strong>Imposto Novo:</strong> R$ {resultado.imposto_novo.toFixed(2)}</p>
          <p><strong>Diferen√ßa:</strong> R$ {resultado.diferenca.toFixed(2)}</p>
          <p><strong>Varia√ß√£o Percentual:</strong> {resultado.percentual_diferenca.toFixed(2)}%</p>
          <h3>Explica√ß√µes</h3>
          <p><strong>Regime Atual:</strong> {resultado.explicacao_atual}</p>
          <p><strong>Regime Novo:</strong> {resultado.explicacao_novo}</p>
        </div>
      )}
    </div>
  );
};

export default App;
